import { Project, Member, Org } from ".";
/** 套餐方案 */
export declare namespace Membership {
    enum InterestType {
        /** 套餐个人版 */
        personal = 1,
        /** 套餐团队版 */
        team = 2
    }
    /** 权益类型*/
    enum ContentType {
        /** 单项目成员数 */
        ProjectMembers = "project_members",
        /** 团队席位 */
        OrgMembers = "org_members",
        /** 存储容量 */
        Storage = "storage",
        /** 沉默组织 */
        OrgSilence = "org_silence"
    }
    interface ProjectItem {
        created_at: string;
        id: string;
        /**
         * 成员的身份信息
         * {@link Org.HasJobOrgMember}
         */
        org_member: Org.HasJobOrgMember;
        /** 组织成员id */
        org_member_id: string;
        /** 昵称 */
        nickname: string;
        /** 所在项目id */
        project_id: string;
        /**
         * 角色权限
         * {@link Member.Ext.Role}
         */
        role: Member.Ext.Role;
        /** 所在项目信息 */
        project: Project.Item;
        /** 是否为共同项目（仅在传参检测时才会检测，默认为false */
        is_together: boolean;
    }
    /** 套餐权益内容子项 */
    interface RightsContentItem {
        /** 权益类型：project_members: '单项目成员数',org_members: '团队席位', capacity: '存储容量',*/
        content_type: ContentType;
        /** 当前权益生效时间 */
        effective_at: string;
        /** 当前权益过期时间 */
        expiration_at: string;
        /** 权益名称 */
        name: string;
        /** 权益内容总量 */
        total: number;
        /** 权益内容已使用量 */
        used: number;
    }
    /** 申请试用参数 */
    interface TrialParams {
        company_name: string;
        email: string;
        phone: string;
        user_name: string;
    }
    /** 申请试用返回值 */
    interface RightsTrial {
        code: number;
        data: string;
        message: string;
        path: string;
        requestId: string;
        status: string;
        trace_id: string;
    }
    /** 套餐权益内容列表 */
    interface UserRights {
        list: UserRights;
        data: {
            list: UserRightsItem[];
        };
    }
    /** 套餐权益内容 */
    interface UserRightsItem {
        /** 套餐权益内容 */
        content: RightsContentItem[];
        /** 当前权益生效时间 */
        effective_at: string;
        /** 当前权益过期时间 */
        expiration_at: string;
        /** 是否是当前生效版本 */
        is_actived: boolean;
        /** 权益名称 */
        name: string;
        /** 套餐团队版还是个人版；1个人；2团队 */
        interest_type: InterestType;
        /** 权益英文名，对应套餐里面的good_name */
        interest_sub_type: Org.Ext.InterestSubType;
        /** 过期时间小于7天标红*/
        expiration_highlight: string;
    }
    /** 具体套餐信息*/
    interface SkuItem {
        /** sku title */
        title: string;
        /** 排序 */
        order: number;
        /** 总价格 */
        pay_amount: string;
        show_amount: string;
        origin_amount: string;
        promotion: {
            desc: string;
            title: string;
            code: string;
            type: string;
            start_time: string;
            end_time: string;
            amount: string;
        };
        /** 绑定的商品id */
        good_id: string;
        /** 商品天数 （小时） */
        effective_period: string;
        origin_effective_period: string;
    }
    /** 套餐支付列表*/
    interface PayItem {
        /** 名称 */
        title: string;
        /** 排序 */
        order: number;
        /** 商品类型 person 个人 team 团队 upsale 增值 */
        type: number;
        category: string;
        /** 团队人数 */
        project_members: string;
        /** 空间大小 （gb） */
        space: string;
        /** 数量 */
        quantity: string;
        /** 商品英文名 */
        good_name: string;
        team_seats: string;
        project_num: string;
        /** 商品id */
        id: string;
        /** 若商品为upsale商品，与他有绑定关系的商品id */
        parent_id: string;
        /** 预留 商品属性 */
        attribute: string;
        /** 不同时长的商品价格和优惠策略 */
        sku: SkuItem[];
    }
    /** 套餐列表*/
    interface PayList {
        upsale: PayItem[];
        team: PayItem[];
        person: PayItem[];
    }
    interface RoleItem {
        count: string;
        role: string;
    }
    /** 套餐类型列表*/
    interface BusinessPlan {
        extra: {
            /** 个人免费版防翻录 */
            person_free_custom_watermark: string;
            /** 个人免费版导出批注文档*/
            person_free_export_comments: string;
            team_max_org_members: string;
        };
        person: {
            mouth: BusinessPlanPerson;
            quarter: BusinessPlanPerson;
            year: BusinessPlanPerson;
        };
        team: {
            year: BusinessPlanTeam;
        };
    }
    /** 个人套餐列表*/
    interface BusinessPlanPerson {
        /** 个人免费版*/
        person_free: BusinessItemInfo;
        /** 个人专业版*/
        person_pro: BusinessItemInfo;
        /** 个人基础版*/
        person_standard: BusinessItemInfo;
        /** 个人旗舰版*/
        person_ultimate: BusinessItemInfo;
    }
    /** 团队套餐列表*/
    interface BusinessPlanTeam {
        /** 团队体验版*/
        team_trial: BusinessItemInfo;
        /** 团队专业版*/
        team_pro: BusinessItemInfo;
        /** 团队基础版*/
        team_standard: BusinessItemInfo;
        /** 团队旗舰版*/
        team_ultimate: BusinessItemInfo;
    }
    /** 套餐详细信息*/
    interface BusinessItemInfo {
        /** 额外媒体信息*/
        advanced_media_info: boolean;
        /** 音频波形图批注附件*/
        audio_comment_attach: boolean;
        /**促销开始时间 */
        began_at: string;
        /**高级媒体信息 */
        custom_comments_visible: boolean;
        /** 防翻录 */
        custom_watermark: boolean;
        /**描述 */
        description: string;
        /**推荐语 */
        recommend: string;
        /**套餐code */
        sku_code: string;
        /** 商品ID */
        good_id: string;
        /**促销结束时间 */
        ended_at: string;
        /**导出批注文档 */
        export_comments: boolean;
        /**海量媒体格式预览 */
        mass_media_format: boolean;
        /**资源库最大容量 */
        max_capacity: number;
        /**团队席数 */
        max_org_members: number;
        /** 单项目成员数*/
        max_project_members: number;
        /** 项目数量*/
        max_project_nums: number;
        /** 特殊文件大小*/
        max_special_file_size: number;
        /** 高清播放*/
        media: string;
        /** 多版本批注合并查看*/
        multi_version_comment: boolean;
        /** 名称*/
        name: string;
        /**在线协作文档 */
        online_document: boolean;
        /**原价（虚拟价格） */
        original_amount: string;
        /**支付价 */
        pay_amount: string;
        /** PD/SAI源文件预览*/
        pd_sai: boolean;
        /** 促销label*/
        sale_label: string;
        /** 分享管理*/
        share_manage: boolean;
        /** 优惠价 */
        show_amount: string;
        /** 智能文件夹*/
        smart_folders: boolean;
        /**团队通讯录 */
        team_address_book: boolean;
        /**团队资源库 */
        team_repository: boolean;
    }
}
